FDistribution <- function(
    DistributionFunction = FALSE,
    QuantileFunction = FALSE,
    DistributionFunctionType = "lower",
    x1 = 1,
    p = 0.5,
    x2 = 2,
    dp1 = 3,
    dp2 = 10,
    dp3 = 0) {

    if ( ! requireNamespace("jmvcore", quietly=TRUE))
        stop("FDistribution requires jmvcore to be installed (restart may be required)")


