        DistributionFunction = DistributionFunction,
        QuantileFunction = QuantileFunction,
        DistributionFunctionType = DistributionFunctionType,
        x1 = x1,
        p = p,
        x2 = x2,
        dp1 = dp1,
        dp2 = dp2,
        dp3 = dp3)

    analysis <- FDistributionClass$new(
        options = options,
        data = data)

    analysis$run()

